    //     console.log(data)
    // }


const test =  {
    "apikey" : "18WH7YhvxJb26UVZSIJhozKpThuhP7k7TnERRxnHCQMecr9bjrbbAi9zRDa3mo5bP1RtWtA"
    ,"action" : "member.set"
    ,"email": "trohin.danil2015@yandex.ru"
    ,"addr_type" : "email"
    ,"source" : "81.23.182.239"
    ,"newbie.confirm": "0"
    ,"datakey" : [
        [ "base.firstName", "set", "dima" ]
