            ,"action" : "member.set"
            ,"email": {email}
            ,"addr_type" : "email"
            ,"source" : "81.23.182.239"
            ,"newbie.confirm": "0"
            ,"datakey" : [
                [ "base.firstName", "set", {name} ]
            ]
            ,"return_fresh_obj" : "0"
