import React, { useState, useEffect } from 'react';
import classNames from 'classnames';

import Styles from './CheckBox.module.scss';

const CheckBox = ({ type = 'checkbox', name = '', disabled = false, checked = false, ariaLabel = '' }) => {
    const [isChecked, setIsChecked] = useState(checked);

    useEffect(() => {
        setIsChecked(checked);
    }, [checked]);

    const handleChange = () => {
        if (!disabled) {
            setIsChecked((prev) => !prev);
        }
    };

    return (
        <div className={classNames(type === 'radio' ? Styles.Radio : Styles.CheckBox)}>
            <input
                className={disabled ? Styles.Disabled : null}
                type={type}
                name={name}
                checked={isChecked}
                onChange={handleChange}
                disabled={disabled}
                aria-label={ariaLabel}
            >
                <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 20 20" fill="none">
                    <rect x="0.5" y="0.5" width="19" height="19" rx="3.5" stroke="url(#paint0_angular_1140_173)"/>
                    <defs>
                        <radialGradient id="paint0_angular_1140_173" cx="0" cy="0" r="1" gradientUnits="userSpaceOnUse" gradientTransform="translate(10 10) rotate(90) scale(10)">
                            <stop stop-color="white"/>
                            <stop offset="1" stop-color="white" stop-opacity="0"/>
                        </radialGradient>
                    </defs>
                </svg>
            </input>
        </div>
    );
};

export default CheckBox;
